interface Keypad {
    [index: string]: string[];

}

function codeBreaker(input: string) {
    const keypad: Keypad = {
        1: ['R', 'D'],
        2: ['R', 'D', 'L'],
        3: ['L', 'D'],
        4: ['R', 'U', 'D'],
        5: ['R', 'U', 'D', 'L'],
        6: ['L', 'U', 'D'],
        7: ['R', 'U'],
        8: ['R', 'U', 'L'],
        9: ['L', 'U'],
    };

    let currentKey = '5', result = '';

    input.split('\n').forEach(command => {
        for (let i = 0; i < command.length; i++) {
            const direction = command[i];
            if (!keypad[currentKey].includes(direction)) continue;
            let newKey;

            switch (direction) {
                case 'R':
                    newKey = Number(currentKey) + 1;
                    break;
                case 'L':
                    newKey = Number(currentKey) - 1;
                    break;
                case 'U':
                    newKey = Number(currentKey) - 3;
                    break;
                case 'D':
                    newKey = Number(currentKey) + 3;
                    break;
                default:
                    throw new Error()
            }
            currentKey = newKey.toString();
        }
        result += currentKey;
    })
    console.log(result);


}

